mapper.select:

select

  distinct


from transfer_record




  order by ${orderByClause}


  limit #{limitStart} , #{limitEnd}

    select count(DISTINCT id),name FROM(
select  count(distinct transfer_record_collection_zhifubao_id,transfer_record_collection_name) as num,transfer_record_collection_name as name,transfer_record_collection_zhifubao_id as id
      from transfer_record where transfer_record_collection_name like #{name}
			GROUP BY transfer_record_collection_name,transfer_record_collection_zhifubao_id
UNION ALL
select  count(distinct transfer_record_pay_zhifubao_id,transfer_record_pay_name)as num,transfer_record_pay_name as name,transfer_record_pay_zhifubao_id
        from transfer_record where transfer_record_pay_name like #{name}
				GROUP BY transfer_record_pay_name,transfer_record_pay_zhifubao_id
) as temp where name!='null' and name!='\\N' GROUP BY name having count(DISTINCT id)>1
    limit #{pageIndex},#{pageSize}

select count(*) FROM(
    select count(DISTINCT id),name FROM(
select  count(distinct transfer_record_collection_zhifubao_id,transfer_record_collection_name) as num,transfer_record_collection_name as name,transfer_record_collection_zhifubao_id as id
      from transfer_record where transfer_record_collection_name like #{name}
			GROUP BY transfer_record_collection_name,transfer_record_collection_zhifubao_id
UNION ALL
select  count(distinct transfer_record_pay_zhifubao_id,transfer_record_pay_name)as num,transfer_record_pay_name as name,transfer_record_pay_zhifubao_id
        from transfer_record where transfer_record_pay_name like #{name}
				GROUP BY transfer_record_pay_name,transfer_record_pay_zhifubao_id
) as temp where name!='null' and name!='\\N' GROUP BY name having count(DISTINCT id)>1
) as temp2

    SELECT DISTINCT name from(
select transfer_record_pay_name as name from transfer_record
where transfer_record_collection_name = #{names} and transfer_record_pay_name!=#{names}
UNION ALL
select transfer_record_collection_name as name from transfer_record
where transfer_record_pay_name = #{names} and transfer_record_collection_name!=#{names}
)as temp where name!='null'

    select DISTINCT id from(
select transfer_record_collection_zhifubao_id as id from transfer_record where transfer_record_collection_name=#{name}
UNION ALL
select transfer_record_pay_zhifubao_id from transfer_record where transfer_record_pay_name=#{name}
) as temp
